import { Component } from "react";
import Navigation from "../components/Navigation";
import SettingsModal from "../components/SettingsModal";
import { connect } from "react-redux";

import { SetSettings, SetCredential } from "../redux/action";

class MainLayout extends Component {
  state = {
    showModal: false,
  };

  showModal = () => {
    this.setState({
      ...this.state,
      showModal: !this.state.showModal,
    });
  };

  createSettings = (e, data) => {
    e.preventDefault();
    this.props.SetSettings(data);
    dispatch(SetSettings(data));
  };

  render() {
    return (
      <>
        <Navigation Show={this.showModal} />
        {this.props.children}
        {this.state.showModal && <SettingsModal />}
      </>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    SetSettings: (data) => dispatch(SetSettings(data)),
  };
};

export default connect(null, mapDispatchToProps)(MainLayout);
