import { Component } from "react";
import Navigation from "../components/Navigation";
import SettingsModal from "../components/SettingsModal";
import { connect } from "react-redux";

import { SetSettings, SetCredential } from "../redux/action";

class MainLayout extends Component {
  state = {
    showModal: false,
    token: "",
  };

  showModal = () => {
    this.setState({
      ...this.state,
      showModal: !this.state.showModal,
      token: "",
    });
  };

  createSettings = (data) => {
    this.setState({ ...this.state, showModal: !this.state.showModal });
    this.props.SetSettings(data);
  };

  getChallenge() {
    let a = FetchData(this.props.settings.url, {
      operation: "getchallenge",
      username: this.props.settings.user,
    }).then((res) => {
      if (res.success) {
        this.setState({ ...this.state, token: res.result.token });
      }
    });
  }

  componentDidUpdate(prevProps) {
    if (prevProps.settings !== this.props.settings) {
      this.getChallenge();
    }
  }

  render() {
    return (
      <>
        <Navigation Show={this.showModal} />
        {this.props.children}
        {this.state.showModal && (
          <SettingsModal SetSettings={this.createSettings} />
        )}
      </>
    );
  }
}

let FetchData = (url, data) => {
  return new Promise((resolve, reject) => {
    fetch(url + `/webservice.php?${encodeFormData(data)}`, {
      header: {
        "Content-Type": "application/x-www-form-urlencoded",
        Mode: "no-cors",
      },
    })
      .then((res) => {
        try {
          if (res.ok) return res.json();
        } catch (err) {
          console.warn(e);
        }
      })
      .then((data) => {
        resolve(data);
      });
  });
};

const encodeFormData = (data) => {
  return Object.keys(data)
    .map((key) => encodeURIComponent(key) + "=" + encodeURIComponent(data[key]))
    .join("&");
};

const mapDispatchToProps = { SetSettings };

const mapStateToProps = (state) => {
  console.log(state);
  return {
    settings: state.settings,
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(MainLayout);
